import math

def es_primo(numero: int) -> bool:
    """
    Verifica si un n√∫mero es primo.

    Args:
        numero (int): El n√∫mero a verificar.

    Returns:
        bool: True si el n√∫mero es primo, False en caso contrario.
    """
    if numero <= 1:
        return False

    for i in range(2, math.isqrt(numero) + 1):
        if numero % i == 0:
            return False

    return True

